question.algorithm:
  $mass_g = 500;
$mass = $mass_g / 1000;
$spring_k = 120;
$x_0 = 0.3;
$v_0 = 6;
$ang_freq = sqrt($spring_k / $mass);
$amplit = sqrt($x_0^2 + $v_0^2/$ang_freq^2);
$phi_sin = $x_0/$amplit;
$phi_cos = $v_0/($amplit*$ang_freq);$phi=if(gt($phi_sin,0),arcsin($phi_sin),Pi - arcsin($phi_sin));
$phi = if(gt($phi_cos, 0), arcsin($phi_sin), Pi - arcsin($phi_sin));
$x_t = $amplit*sin($ang_freq*t + $phi);
